actionCellButton.customFunction:
var deviceService = widgetContext.deviceService;
if (entityId.entityType === 'DEVICE') {
    deviceService.getDevice(entityId.id, {ignoreLoading: true}).subscribe(
        function (device) {
            var deviceType = device.type;
            var params = {
                entityId: entityId,
                entityName: entityName
            };
            var targetDashboardStateId;
            switch (deviceType) {
              case "energy-meter":
                  targetDashboardStateId = "energy_meter";
                  break;
              case "hvac":
                  targetDashboardStateId = "hvac";
                  break;
              case "smart-sensor":
                  targetDashboardStateId = "smart_sensor";
                  break;
              case "water-meter":
                  targetDashboardStateId = "water_meter";
                  break;
            }
            if (targetDashboardStateId) {
                  widgetContext.stateController.openState(targetDashboardStateId, params, false);
            }
        }
    );
}

