settings.cardCss:
.card,
.card * {
    box-sizing: border-box;
}    
.card {
    position: relative;
    width: 100%;
    height: 100%;
}
.card .content {
    height: 100%;
}
.card .upper-bar {
    position: relative;
    z-index: 0;
    overflow: hidden;
    height: 70%;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,.26);
    color: #fff;
}
.card .lower-bar {
    height: 30%;
    padding-left: 16px;
    display: flex;
    flex-direction: row;
    align-items: center;
}

.card .right-top-bar {
    display: none;
    position: absolute;
    top: 10px;
    right: 10px;
    display: flex;
    flex-direction: row;
    align-items: center;
}

.card .right-top-bar .icon-container {
    display: none;
    box-shadow: 0px 0px 4px 2px rgba(0,0,0,.26);
    cursor: pointer;
    border: 3px solid #fff;
    color: #fff;
    background-color: rgb(0, 132, 214);
